CREATE TABLE IF NOT EXISTS `persons` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'Internal ID',
  `name` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Name',
  `phone` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Phone',
  `email` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Email',
  `birthdate` date DEFAULT NULL COMMENT 'Birthdate',
  `address` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Address',
  `city` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'City',
  `zip` varchar(10) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'ZIP code',
  `country` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Country',
  PRIMARY KEY (`id`),
  KEY `name` (`name`),
  KEY `email` (`email`),
  KEY `zip` (`zip`),
  KEY `phone` (`phone`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='Persons' AUTO_INCREMENT=101 ;



